mapper.select:
SELECT aa.adopt_apply_id AS adoptApplyId, a.animal_name AS animalName, aa.member_id AS memberId, m.member_name AS memberName, aa.adopt_apply_document_id AS adoptApplyDocumentId FROM adopt_apply AS aa, animal AS a, member AS m
aa.animal_id = a.animal_id AND aa.member_id = m.member_id AND a.shelter_id = #{shelterId} AND aa.apply_reject_date IS NULL AND (SELECT aa.adopt_apply_id FROM adopt AS ad WHERE ad.adopt_apply_id = aa.adopt_apply_id) IS NULL
AND a.animal_name LIKE CONCAT('%', #{searchWord},'%');

SELECT aa.adopt_apply_id AS adoptApplyId, a.animal_category_id AS animalCategoryId, a.animal_id AS animalId, a.animal_name AS animalName, m.member_id AS memberId, m.member_name AS memberName, aa.adopt_apply_document_id AS adoptApplyDocumentId, DATE(aa.apply_date) AS applyDate, DATE(ad.adopt_date) AS adoptDate, ad.staff_id AS staffId FROM adopt AS ad, adopt_apply AS aa, animal AS a, member AS m
ad.adopt_apply_id = aa.adopt_apply_id AND aa.animal_id = a.animal_id AND aa.member_id = m.member_id AND a.shelter_id = #{shelterId}
AND a.animal_name LIKE CONCAT('%', #{searchWord},'%');
AND m.member_name LIKE CONCAT('%', #{searchWord},'%');

SELECT aa.adopt_apply_id AS adoptApplyId, a.animal_name AS animalName, m.member_name AS memberName, m.member_id AS memberId, aa.adopt_apply_document_id AS adoptApplyDocumentId, DATE(aa.apply_date) AS applyDate, DATE(aa.apply_reject_date) AS applyRejectDate FROM adopt_apply AS aa, animal AS a, member AS m
aa.animal_id = a.animal_id AND aa.member_id = m.member_id AND aa.apply_reject_date IS NOT NULL AND a.shelter_id = #{shelterId}
AND a.animal_name LIKE CONCAT('%', #{searchWord},'%');

SELECT aa.adopt_apply_id AS adoptApplyId, a.animal_id AS animalId, a.animal_category_id AS animalCategoryId, a.animal_name AS animalName, m.member_id AS memberId, m.member_name AS memberName, aa.adopt_apply_document_id AS adoptApplyDocumentId, DATE(aa.apply_date) AS applyDate, DATE(ad.adopt_date) AS adoptDate, DATE_ADD(date(ad.adopt_date), INTERVAL 52 week) AS 'regularCk', ci.care_info_id AS careInfoId, ad.staff_id AS staffId FROM adopt AS ad, adopt_apply AS aa, animal AS a, member AS m, animal_category AS ac, care_info AS ci
ad.adopt_apply_id = aa.adopt_apply_id AND aa.animal_id = a.animal_id AND aa.member_id = m.member_id AND ci.animal_category_id = ac.animal_category_id AND ac.animal_category_id = a.animal_category_id AND ci.care_sorting = '정기검진' AND a.shelter_id = #{shelterId} AND (SELECT DISTINCT(cp.animal_id) FROM care_plan AS cp WHERE cp.animal_id = a.animal_id) IS NULL
AND a.animal_id = #{animalId}

mapper.insert:
INSERT INTO adopt(
	adopt_apply_id,
	staff_id,
	adopt_date
) VALUE(
	#{adoptApplyId},
	#{staffId},
	NOW()
);

mapper.update:
UPDATE 
	adopt_apply SET apply_reject_date = NOW()
WHERE adopt_apply_id = #{adoptApplyId};
