mapper.select:
SELECT * FROM volunteer_list
AND shelterName LIKE CONCAT('%',#{searchWord},'%')
AND categoryName = #{categoryName}
ORDER BY CASE WHEN volunteerDate > NOW() THEN 0 ELSE 1 END, volunteerDate LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM volunteer_recruit vr JOIN staff s ON vr.staff_id = s.staff_id JOIN shelter sh ON s.shelter_id = sh.shelter_id JOIN volunteer_category vc ON vr.volunteer_category_id = vc.volunteer_category_id
AND sh.shelter_name LIKE CONCAT('%',#{searchWord},'%')
AND vc.volunteer_category_name = #{categoryName}

SELECT * FROM periodically_volunteer_list
AND shelterName LIKE CONCAT('%',#{searchWord},'%')
AND categoryName = #{categoryName}
ORDER BY CASE WHEN startDate > NOW() THEN 0 ELSE 1 END, startDate LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM periodically_volunteer_recruit pvr JOIN staff s ON pvr.staff_id = s.staff_id JOIN shelter sh ON s.shelter_id = sh.shelter_id JOIN volunteer_category vc ON pvr.volunteer_category_id = vc.volunteer_category_id
AND sh.shelter_name LIKE CONCAT('%',#{searchWord},'%')
AND vc.volunteer_category_name = #{categoryName}

SELECT vr.volunteer_recruit_id AS volunteerRecruitId, vr.staff_id AS staffId, vc.volunteer_category_id AS volunteerCategoryId, vc.volunteer_category_name AS volunteerCategoryName, DATE(vr.volunteer_date) AS volunteerDate, vr.recruit_count AS recruitCount, COUNT(va.volunteer_recruit_id) AS applyCount, DATE(vr.register_date) AS registerDate, vr.volunteer_title AS volunteerTitle FROM volunteer_recruit AS vr LEFT JOIN volunteer_category AS vc ON vr.volunteer_category_id = vc.volunteer_category_id LEFT JOIN staff AS s ON vr.staff_id = s.staff_id LEFT JOIN volunteer_apply AS va ON va.volunteer_recruit_id = vr.volunteer_recruit_id
s.shelter_id = #{shelterId}
AND vr.volunteer_title LIKE CONCAT('%', #{searchWord}, '%')
AND vr.staff_id LIKE CONCAT('%', #{searchWord}, '%')
AND vc.volunteer_category_name = #{categoryName}
GROUP BY vr.volunteer_recruit_id ORDER BY volunteerDate ASC,registerDate DESC

SELECT va.volunteer_apply_id AS volunteerApplyId, m.member_name AS memberName, m.member_id AS memberId, vc.volunteer_category_name AS volunteerCategoryName, vr.volunteer_title AS volunteerTitle, HOUR(vck.end_time-vck.start_time) AS volunteerTime, DATE_FORMAT(vck.start_time, '%H:%i') AS startTime, DATE_FORMAT(vck.start_time, '%H:%i') AS endTime, DATE(vr.volunteer_date) AS volunteerDate, DATE(vck.check_date) AS checkDate, vck.staff_id_check AS staffId FROM volunteer_check AS vck, volunteer_apply AS va, volunteer_recruit AS vr, volunteer_category AS vc, staff AS s, member AS m
vck.volunteer_apply_id = va.volunteer_apply_id AND va.volunteer_recruit_id = vr.volunteer_recruit_id AND vr.volunteer_category_id = vc.volunteer_category_id AND va.member_id = m.member_id AND vck.staff_id_check = s.staff_id AND s.shelter_id = #{shelterId}
AND (m.member_name LIKE CONCAT('%', #{searchWord}, '%') OR m.member_id LIKE CONCAT('%', #{searchWord}, '%'))
AND vc.volunteer_category_name = #{categoryName}

SELECT vc.volunteer_category_id AS volunteerCategoryId, vc.volunteer_category_name AS volunteerCategoryName, pvr.title AS title, pvr.recruit_count AS recruitCount, COUNT(pva.periodically_volunteer_apply_id) AS applyCount, DATE(pvr.periodically_volunteer_start_date) AS startDate, DATE(pvr.register_date) AS registerDate, pvr.volunteer_cycle AS volunteerCycle, pvr.staff_id AS staffId FROM periodically_volunteer_recruit AS pvr LEFT JOIN volunteer_category AS vc ON pvr.volunteer_category_id = vc.volunteer_category_id LEFT JOIN staff AS s ON pvr.staff_id = s.staff_id LEFT JOIN periodically_volunteer_apply AS pva ON pva.periodically_volunteer_recruit_id = pvr.periodically_volunteer_recruit_id
s.shelter_id = #{shelterId}
AND pvr.volunteer_title LIKE CONCAT('%', #{searchWord}, '%')
AND pvr.staff_id LIKE CONCAT('%', #{searchWord}, '%')
AND vc.volunteer_category_name = #{categoryName}
GROUP BY pvr.periodically_volunteer_recruit_id ORDER BY registerDate DESC

SELECT m.member_name AS memberName, m.member_id AS memberId, vc.volunteer_category_name AS volunteerCategoryName, pvr.title AS title, DATE(pvc.volunteer_date) AS volunteerDate, DATE(pvc.check_date) AS checkDate, pvc.staff_id_check AS staffId, pvc.periodically_volunteer_apply_id AS periodVolunteerApplyId FROM periodically_volunteer_check AS pvc, periodically_volunteer_apply AS pva, periodically_volunteer_recruit AS pvr, staff AS s, member AS m, volunteer_category AS vc
pvc.periodically_volunteer_apply_id = pva.periodically_volunteer_apply_id AND pva.periodically_volunteer_recruit_id = pvr.periodically_volunteer_recruit_id AND vc.volunteer_category_id = pvr.volunteer_category_id AND pvc.staff_id_check = s.staff_id AND pva.member_id = m.member_id AND s.shelter_id = #{shelterId}
AND (m.member_name LIKE CONCAT('%', #{searchWord}, '%') OR m.member_id LIKE CONCAT('%', #{searchWord}, '%'))
AND vc.volunteer_category_name = #{categoryName}
